mapper.select:
SELECT r.RESOURCE_NAME NAME, r.RESOURCE_CODE value, r.RESOURCE_LEVEL level, (SELECT IF(COUNT(1) > 0, true, false) FROM permission p WHERE p.DELETED = 0 AND p.PERM_CODE = r.RESOURCE_CODE) DISABLED FROM resource r WHERE r.RESOURCE_CODE IS NOT NULL
AND r.RESOURCE_NAME LIKE #{searchKeyBind} OR r.RESOURCE_CODE LIKE #{searchKeyBind}
ORDER BY r.RESOURCE_NAME

SELECT DISTINCT ROLE_CODE FROM role WHERE DELETED = 0 AND ID IN (
    SELECT ROLE_ID FROM role_permission_relation WHERE DELETED = 0 AND PERM_ID = #{permId}
)

SELECT USER_ID FROM user_role_relation WHERE ROLE_ID IN (
    SELECT ROLE_ID FROM role_permission_relation WHERE DELETED = 0 AND PERM_ID = #{permId}
) AND DELETED = 0
