mapper.select:
SELECT
    r.ID, r.ROLE_NAME, r.ROLE_CODE, r.ROLE_DESC, r.ENABLED, r.CREATE_TIME, r.UPDATE_TIME,
    (SELECT USERNAME FROM user u WHERE u.ID = r.CREATE_USER) AS CREATE_USERNAME,
    (SELECT USERNAME FROM user u WHERE u.ID = r.UPDATE_USER) AS UPDATE_USERNAME
FROM role r
WHERE DELETED = '0'


    AND ROLE_NAME LIKE #{roleNameBind}



    AND ROLE_CODE LIKE #{roleCodeBind}


    AND ENABLED = #{enabled}

ORDER BY CREATE_TIME

SELECT
    u.ID, u.USERNAME, u.NICKNAME, u.EMAIL, u.LOGIN_TIME
FROM user u
INNER JOIN user_role_relation urr ON urr.USER_ID = u.ID
WHERE u.DELETED = '0' AND urr.ROLE_ID = #{businessId}


    AND u.USERNAME LIKE #{usernameBind}



    AND u.NICKNAME LIKE #{nicknameBind}





    AND u.LOGIN_TIME >= CONCAT(#{loginStartTime}, ' 00:00:00')


    AND u.LOGIN_TIME <= CONCAT(#{loginStartTime}, ' 23:59:59')

ORDER BY u.USERNAME, u.NICKNAME

SELECT * FROM role WHERE DELETED = 0 AND ID IN (
    SELECT ROLE_ID FROM user_role_relation WHERE USER_ID = #{userId}
)
