mapper.select:
SELECT
    u.USERNAME, LOGIN_TIME, LOGIN_CHAN, LOGIN_TYPE, LOGIN_IP, LOGIN_ADDRESS, LOGIN_BROWSER, LOGIN_BROWSER_VERSION, LOGIN_PLATFORM, USER_AGENT, IS_SUCCESS, REMARK
FROM user_login_log ull
LEFT JOIN user u ON ull.USER_ID = u.ID


        u.USERNAME = #{query.username}


        AND ull.IS_SUCCESS = #{query.loginResult}


        AND ull.LOGIN_TYPE = #{query.loginType}



        AND ull.LOGIN_ADDRESS LIKE #{loginAddressBind}


        AND ull.LOGIN_IP = #{query.loginIp}


        AND ull.LOGIN_TIME >= #{query.loginStartTime, jdbcType=TIMESTAMP}


        AND ull.LOGIN_TIME >= #{query.loginEndTime, jdbcType=TIMESTAMP}


ORDER BY LOGIN_TIME DESC
