mapper.select:
SELECT
    ID, LOG_TYPE, OPERATE_USER, EVENT_TIME, `DESCRIPTION`,
    REQUEST_PARAM, INTERFACE_CODE, REQUEST_IP, REQUEST_URI, REQUEST_METHOD,
    ADDRESS, BROWSER, COST_TIME, EXEC_RESULT, EXEC_MESSAGE, CREATE_DATE
FROM log
WHERE LOG_TYPE = #{logType}


    AND `DESCRIPTION` LIKE #{descriptionBind}


    AND INTERFACE_CODE = #{interfaceCode}


    AND OPERATE_USER = #{username}


    AND EXEC_RESULT = #{execResult}


    AND COST_TIME >= #{costTime, jdbcType=DOUBLE}


    AND EVENT_TIME >= #{eventStartTime}


    AND EVENT_TIME <= #{eventEndTime}

ORDER BY EVENT_TIME DESC

mapper.delete:
DELETE FROM log WHERE LOG_TYPE = #{logType}


    AND `DESCRIPTION` LIKE #{descriptionBind}


    AND INTERFACE_CODE = #{interfaceCode}


    AND OPERATE_USER = #{username}


    AND EXEC_RESULT = #{execResult}


    AND COST_TIME >= #{costTime, jdbcType=DOUBLE}


    AND EVENT_TIME >= #{eventStartTime}


    AND EVENT_TIME <= #{eventEndTime}
