  `CREATE_USER` BIGINT NOT NULL COMMENT '创建用户',
  `CREATE_TIME` CHAR(19) NOT NULL COMMENT '创建时间',
  `UPDATE_USER` BIGINT DEFAULT NULL COMMENT '更新用户',
  `UPDATE_TIME` CHAR(19) DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`ID`) USING BTREE,
