  INDEX USER_NICKNAME_INDEX (`NICKNAME`),
  INDEX USER_EMAIL_INDEX (`EMAIL`),
  INDEX USER_USER_STATUS_INDEX (`USER_STATUS`),
  INDEX USER_DELETED_INDEX (`DELETED`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='用户表';

